import React, { Component, Fragment } from 'react';
import { BrowserRouter, Route } from 'react-router-dom';
import Home from './Home';
import ProductDetails from './ProductDetails';

import Products from '../src/MOCK_DATA.json';  


class App extends Component{


  constructor(props){
    super(props);
    this.state = {
        prodList: Products,
        tempList:[]
    }

  }

  componentDidMount(){
    {/* Don't mutate the  'nameList', duplicate it and apply operations on it  */}
    let newList=[...this.state.nameList];
    this.setState({
      tempList:newList
    })
  }

  inputChange=(ev)=>{
    let filteredArray=[];
    {/*If search box input is 'nothing' then set 'nameList' data to 'tempList' */}
    if(ev.target.value===''){
      filteredArray=[...this.state.nameList]
    }else{
       let newList=[...this.state.tempList];
       filteredArray=newList.filter(item=>{
       return item.name.toLowerCase().search(
                 event.target.value.toLowerCase()) !== -1
   }) 
  }
    this.setState({
      tempList:filteredArray
    })

   }


  render(){
    return(
      <BrowserRouter>
        <Fragment>
          <Route exact path="/" component={() => (
            < Home prodList={this.state.prodList} />
          )}
          />
          <Route exact path="/detail/:id" component={() => (
            < ProductDetails prodList={this.state.prodList} />
          )} />
        </Fragment>
      </BrowserRouter>

    )
  }
}

export default App;
