import React, { Component } from 'react';
import axios from 'axios';

class Books extends Component {

    constructor(props){
        super(props)
        this.state = {
          books: []
        }
    }

    componentDidMount(){
        axios.get('https://api.myjson.com/bins/tf31e')
        .then(json => json.data.Search.map(result => (
          {

            cover: `${result.Poster}`,
            name: `${result.Title}`,
            year: `${result.Year}`,

          })))
        .then(newData => this.setState({books: newData}))
        .catch(error => alert(error))
    }

    render() {
        const {books} = this.state
        return (
            <React.Fragment>

                <div className="prod-container">
                    <h4 className="title-desc">Books Recommended</h4>
                </div>

                <div className="flex gallery prod-container">

                    {books.map(
                        book =>
                        <div className="book-item">
                            <div>
                                <img src={book.cover} />
                            </div>
                            <div className="legend">
                                <h5>{book.name}</h5>
                                {/* <h6>{book.year}</h6> */}
                            </div>

                        </div>
                    )}
                </div>

            </React.Fragment>
        )
    }
}
export default Books;
